#include<bits/stdc++.h>#define re register// #define int long long#define rep(i,a,b) for(int i(a);i<=(b);++i)#define req(i,a,b) for(int i(a);i>=(b);--i)using namespace std;// char buf[1<<23],*p1=buf,*p2=buf,ubuf[1<<23],*u=ubuf;// #define getchar() (p1==p2&&(p2=(p1=buf)+fread(buf,1,1<<21,stdin),p1==p2)?EOF:*p1++)template<typename TP> inline TP read(TP &num){	re TP x=0;re int f=0;re char ch=getchar();	while(ch<48||ch>57) f|=ch=='-',ch=getchar();	while(48<=ch&&ch<=57) x=(x<<1)+(x<<3)+(ch^48),ch=getchar();	return num=f?-x:x;}template<typename TP> inline void write(TP x){	(x<0)?(putchar('-'),x=-x):0;    (x>9)?(write(x/10),0):0;	putchar((x%10)^48);}template<typename TP> inline void writeln(TP x){	write<TP>(x);	puts("");}int n,m,ans,lc,no,res;bitset<200001> a[23];mt19937 rnd(998244353);signed main(){	freopen("matrix.in","r",stdin);	freopen("matrix.out","w",stdout);	read(n);read(m);	rep(i,1,n) cin>>a[i],ans+=a[i].count();	while(clock()*1.0/CLOCKS_PER_SEC<0.9)	{		lc=rnd()%2;		if(lc)		{			no=rnd()%n+1;			rep(i,1,m) a[no].set(i,!a[no].test(i));		}		else		{			no=rnd()%m+1;			rep(i,1,n) a[i].set(no,!a[i].test(no));		}		//rep(i,1,n){rep(j,1,m)cout<<a[i][j];cout<<endl;}		res=0;		rep(i,1,n) rep(j,1,m) res+=a[i].test(j);		ans=min(ans,res);	}	writeln(ans);	return 0; }